00873     /**
00874      * mShowProgression méthode outils d'affichage de progression
00875      */ 
00876     private void mShowProgression(int pCounter, int pSize, int pModulo)
00877     {
00878         if((pCounter % pModulo == 0) || (pCounter >= pSize - 1))
00879         {
00880             System.out.println(pCounter + " / " + (pSize - 1));
00881         }
00882         pCounter++;
00883     }
