01033     public void mF1_Circuits()
01034     {
01035         System.out.print("Filling table F1_Circuits... ");
01036         List<Map<String, String>> vCircuitsData = this.mReadFile("circuits.csv");
01037         List<Location> vLocations = this.mLocations();
01038                 
01039         if(vCircuitsData != null)
01040         {
01041             String vSQL = "INSERT INTO F1_Circuits "
01042                     + "("
01043                         + "IDCircuit, "
01044                         + "CircuitRefCircuit, "
01045                         + "NameCircuit, "
01046                         + "LatitudeCircuit, "
01047                         + "LongitudeCircuit, "
01048                         + "Altitude, "
01049                         + "URL, "
01050                         + "IDLocation"
01051                     + ") "
01052                     + "Values "
01053                     + "("
01054                         + "?,?,?,?,?,?,?,?"
01055                     + ");";
01056             int vSize = vCircuitsData.size();
01057             int vCounter = 0;   
01058             for(Map<String, String> vRow : vCircuitsData)
01059             {
01060                 this.mShowProgression(vCounter, vSize, 10);
01061                 vCounter++;
01062                 String vCircuitID = vRow.get("circuitId");
01063                 String vCircuitRef = vRow.get("circuitRef");
01064                 String vCircuit = vRow.get("name");
01065                 String vCircuitLocation = vRow.get("location");
