01071                 
01072                 if
01073                 (
01074                     (vCircuitID != null) 
01075                     && 
01076                     (vCircuitRef != null) 
01077                     && 
01078                     (vCircuit != null) 
01079                     && 
01080                     (vCircuitLocation != null) 
01081                     && 
01082                     (vCircuitCountry != null) 
01083                     && 
01084                     (vCircuitLatitude != null) 
01085                     && 
01086                     (vCircuitLongitude != null) 
01087                     && 
01088                     (vCircuitAltitude != null) 
01089                     && 
01090                     (vCircuitURL != null)
01091                 )
01092                 {
01093                     vCircuit = this.mPurifyCircuitsNames(vCircuit);
01094                     vCircuitLocation = this.mPurifyLocationName(vCircuitLocation);
01095                     vCircuitCountry = this.mPurifyCountryName(vCircuitCountry);
