01337     /**
01338      * mPurifyTime méthode outils de nétoyage des heures du fichier CSV
01339      * @param pTime Heure à netoyer
01340      * @return String heure du fichier CSV nétoyé.
01341      */
01342     private String mPurifyTime(String pTime)
01343     {
01344         if(pTime.contains("sec"))
01345         {
01346             pTime = pTime.replace("sec", "").trim();
01347         }
01348         if(pTime.contains("s"))
01349         {
01350             pTime = pTime.replace("s", "").trim();
01351         }
01352         if(pTime.contains("+"))
01353         {
01354             pTime = pTime.replace("+", "").trim();
01355         }
01356         return pTime;
01357     }
