01359     /**
01360      * mTimeToDouble Convertis une heure du fichier CSV en Double objet
01361      * @return Double heure du fichier CSV convertie en Double.
01362      */
01363     private Double mTimeToDouble(String pTime)
01364     {
01365         Double vResult = null;
01366         pTime = this.mPurifyTime(pTime);
01367         
01368         if(pTime.contains(":"))
01369         {
01370             vResult = Double.valueOf(pTime.split(":")[0]) * 60.0 + Double.valueOf(pTime.split(":")[1]);
01371         }
01372         else
01373         {
01374             vResult = Double.valueOf(pTime);
01375         }
01376         return vResult;     
01377     }
